#!/usr/bin/R
library(patchwork)
library(ggplot2)
a<- read.table("hemp.lai.result.txt",header = T,sep = "\t")#,colClasses = c("character", "numeric", "numeric", "numeric", "numeric", "character"), na.strings = "NA")
a$lai <- as.numeric(a$lai)
a$pos <- as.numeric(a$pos)
a1 <- subset(a,a$type=="chr1")
a2 <- subset(a,a$type=="chr2")
a3 <- subset(a,a$type=="chr3")
a4 <- subset(a,a$type=="chr4")
a5 <- subset(a,a$type=="chr5")
a6 <- subset(a,a$type=="chr6")
a7 <- subset(a,a$type=="chr7")
a8 <- subset(a,a$type=="chr8")
a9 <- subset(a,a$type=="chr9")
aX <- subset(a,a$type=="chrX")

p1 <- ggplot(a1,aes(x=pos,y=lai,color=id)) + geom_line() + xlim(0,208) + scale_color_brewer(palette = "Set2") + theme_bw() + theme(panel.background = element_blank(),axis.text.x = element_blank(),legend.position = "none",axis.title.y = element_blank()) + xlab("Chr01")+ylim(0,40)
p2<- ggplot(a2,aes(x=pos,y=lai,color=id)) + geom_line()  + scale_color_brewer(palette = "Set2") + theme_bw() + theme(panel.background = element_blank(),axis.text.x = element_blank(),legend.position = "none",axis.title.y = element_blank(),axis.text.y = element_blank()) + xlab("Chr02")+ xlim(0,255)+ylim(0,40)
