p5 <- ggplot(a5,aes(x=pos,y=lai,color=id)) + geom_line()  + scale_color_brewer(palette = "Set2") + theme_bw() + theme(panel.background = element_blank(),axis.text.x = element_blank(),axis.title.y = element_blank(),axis.text.y = element_blank()) + xlab("Chr05")+ xlim(0,258)+ylim(0,40)
p6<-  ggplot(a6,aes(x=pos,y=lai,color=id)) + geom_line()  + scale_color_brewer(palette = "Set2") + theme_bw() + theme(panel.background = element_blank(),axis.text.x = element_blank(),legend.position = "none",axis.title.y = element_blank()) + xlab("Chr06")+ xlim(0,255)+ylim(0,40)
p7 <- ggplot(a7,aes(x=pos,y=lai,color=id)) + geom_line()  + scale_color_brewer(palette = "Set2") + theme_bw() + theme(panel.background = element_blank(),axis.text.x = element_blank(),legend.position = "none",axis.title.y = element_blank(),axis.text.y = element_blank()) + xlab("Chr07")+ xlim(0,214)+ylim(0,40)
p8 <- ggplot(a8,aes(x=pos,y=lai,color=id)) + geom_line()  + scale_color_brewer(palette = "Set2") + theme_bw() + theme(panel.background = element_blank(),axis.text.x = element_blank(),legend.position = "none",axis.title.y = element_blank(),axis.text.y = element_blank()) + xlab("Chr08")+ xlim(0,172)+ylim(0,40)
p9<- ggplot(a9,aes(x=pos,y=lai,color=id)) + geom_line()  + scale_color_brewer(palette = "Set2") + theme_bw() + theme(panel.background = element_blank(),axis.text.x = element_blank(),legend.position = "none",axis.title.y = element_blank(),axis.text.y = element_blank()) + xlab("Chr09")+ xlim(0,204)+ylim(0,40)
